# Print all even numbers from 1 to 20
for i in range(1, 21):
    if i % 2 == 0:
        print(i)

# Write a function to return factorial
def factorial(n):
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n-1)

print(factorial(5))

